[
  {{setVar 'wsInList' (jsonParse (body 'soapenv:Envelope.soapenv:Body.urn:getWebPricing.urn:wsInList._cdata'))}}
  {{log (jsonParse (data 'lek6' '_request'))}}
  {{setVar 'uomOptions' (array 'MTR' 'EA' 'PK' 'RO' 'PAC' 'PKT')}}
  {{setVar 'qtyOptions' (array 100 50 10 1)}}
  {{setVar 'fromOptions' (array 'contract' 'orpc' 'poprice')}}
  {{setVar 'depOptions' (array 0 78)}}{{setVar 'ratioOptions' (array 100 0.1)}}
  {{#each (jsonParse (dataRaw 'lek6' '_request.soapenv:Envelope.soapenv:Body.urn:getWebPricing.urn:wsInList._cdata'))}}
  {{setVar 'uom' (oneOf (getVar 'uomOptions'))}}
  {{setVar 'mult_qty' (oneOf (getVar 'qtyOptions'))}}
  {{setVar 'ratioconv' (oneOf (getVar 'ratioOptions'))}}
  {{setVar 'dperc' (oneOf (getVar 'depOptions'))}}
  {{setVar 'uprice' (faker 'finance.amount' min=0.01 max=1000 dec=4)}}
  {{setVar 'fprice' (toFixed (multiply (getVar 'uprice') (subtract 1 (divide (getVar 'dperc') 100))) 4)}}
  {{setVar 'tprice' (toFixed (multiply (getVar 'uprice') (getVar 'mult_qty')) 4)}}
  {{setVar 'alias_uprice' (toFixed (multiply (getVar 'uprice') (getVar 'mult_qty')) 4)}}
  {{setVar 'alias_fprice' (toFixed (multiply (getVar 'fprice') (getVar 'mult_qty')) 4)}}
  {{setVar 'aliasm_uprice' (toFixed (multiply (getVar 'uprice') (getVar 'mult_qty')) 4)}}
  {{setVar 'aliasm_fprice' (toFixed (multiply (getVar 'fprice') (getVar 'mult_qty')) 4)}}
  {{setVar 'aliasm_tprice' (toFixed (multiply (getVar 'tprice') (getVar 'mult_qty')) 4)}}
  {
    "sku": "{{this.sku}}",
    "error": null,
    "from": "contract",
    "uom": "{{getVar 'uom'}}",
    "is_poa": false,
    "iqty": 1,
    "uprice": {{ getVar 'uprice' }},
    "dperc": {{ getVar 'dperc' }},
    "fprice": {{ getVar 'fprice' }},
    "saqty": 1,
    "sauom": "{{getVar 'uom'}}",
    "saconv": 1,
    "samult": true,
    "prqty": 0.01,
    "pruom": "{{getVar 'uom'}}",
    "prconv": 100,
    "prmult": false,
    "breaks": [
      {{#each (array 100 200 300)}}
      {
        "qty": {{this}},
        "label": "{{this}}+",
        "bdisc": 1.5,
        "uprice": {{faker 'finance.amount' min=0.01 max=100 precision=2}}
      }{{#unless @last}},{{/unless}}
      {{/each}}
    ],
    "split": null,
    "ordermult": {
      "qty": {{getVar 'mult_qty'}},
      "valid_qty": false,
      "uprice": {{ getVar 'uprice' }},
      "tprice": {{ getVar 'tprice' }},
      "fprice": {{ getVar 'fprice' }}
    },
    "aliases": [
      {{#each (array 3 4 5)}}
      {
        "sku": "{{faker 'string.numeric' length=9 }}",
        "uom": "{{getVar 'uom'}}",
        "conv": {{getVar 'ratioconv'}},
        "mult": false,
        "selected": false,
        "ratio": {{getVar 'ratioconv'}},
        "uprice": {{ getVar 'alias_uprice' }},
        "fprice": {{ getVar 'alias_fprice' }},
        "iuom": {
          "qty": {{getVar 'mult_qty'}},
          "uprice": {{ getVar 'uprice' }},
          "fprice": {{ getVar 'fprice' }}
        },
        "breaks": [],
        "split": null,
        "ordermult": {
          "qty": 1,
          "valid_qty": true,
          "uprice": {{ getVar 'aliasm_uprice' }},
          "tprice": {{ getVar 'aliasm_tprice' }},
          "fprice": {{ getVar 'aliasm_fprice' }}
        }
      }{{#unless @last}},{{/unless}}
      {{/each}}
    ]
  }{{#unless @last}},{{/unless}}
  {{/each}}
]
